import React, { useState } from 'react';
import "./AddFunds.css"
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useSnackbar } from 'notistack';
const API_URL = "http://localhost:8080/wallets/";

function AddFunds(props) {
  const { enqueueSnackbar } = useSnackbar();
  const [amount, setAmount] = useState('');
  const wallet = props.wallet;
  
  function handleSubmit(event) {
    event.preventDefault();
    console.log(amount);
    setAmount('');
  }

  const onAmountChangeHandler = (event)=>{
    setAmount(event.target.value);
  }
  const handleBackClick = (event)=>{
    event.preventDefault();
    axios.post(API_URL + `${wallet.username}/balance`,amount).then(
        (response) => {enqueueSnackbar('Transfer completed successfully', { variant: 'success' });
             
    }
    );
    props.onClose();
  }
  return (
    <div className="overlay">
      <div className="overlay-content">
        <h2>Add Funds</h2>
        <form className="add-funds-form" onSubmit={handleSubmit}>
          <label htmlFor="amount">Amount:</label>
          <input type="number" id="amount" name="amount" onChange={onAmountChangeHandler}/>


          <button type="submit" className="add-funds-btn">
            Add Funds
          </button>
        </form>

        <button type="button" className="back-btn" onClick={handleBackClick}>
          Back
        </button>
      </div>
    </div>
  );

}

export default AddFunds;
