import React, { useState } from 'react';
import './TransferFunds.css';
import { transfer } from '../../slices/walletslice';
import { useDispatch,useSelector} from "react-redux";

function TransferFunds(props) {
  const dispatch = useDispatch();
  const [username, setUsername] = useState('');
  const [amount, setAmount] = useState(0);
  const { user: currentUser } = useSelector((state) => state.auth);
  const senderUsername = currentUser.username;


  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(`Transferring ${amount} to ${username}`);
    dispatch(transfer({ senderUsername, username, transferAmount:amount }))
      .unwrap()
      .then(() => {
      })
      .catch(() => {
      });
    props.onClose();
};

  return (
    <div className="overlay">
      <div className="form-container">
        <h2>Transfer Funds</h2>
        <form className="add-funds-form" onSubmit={handleSubmit}>
          <label htmlFor="username">Username</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />

          <label htmlFor="amount">Amount</label>
          <input
            type="number"
            id="amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            required
          />

          <div className="button-container">
            <button className="add-button" type="submit">
              Transfer Funds
            </button>
            <button className="back-button" onClick={props.onClose}>
              Back
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default TransferFunds;
