import React,{useState,useEffect} from "react";
import { useSelector } from "react-redux";
import axios from "axios";
import "./wallet.css";
import AddFunds from "./AddFunds";
const API_URL = "http://localhost:8080/wallets/";
const Wallet = () => {
    const [wallet,setWallet] = useState(null);
    const [addFunds,setAddFunds] = useState(false);
    const [amount, setAmount] = useState(0);
    const { user: currentUser } = useSelector((state) => state.auth);
    useEffect(()=>{
        axios.get(API_URL + `${currentUser.username}`).then(
            (response) => {setWallet(response.data);console.log(response.data)}
        );
    },[currentUser.username]);
    function handleSubmit(event) {
        event.preventDefault();
        console.log(amount);
        console.log(API_URL + `${wallet.username}/balance`);
        axios.post(API_URL + `${wallet.username}/balance`,{balance:amount}).then(
            (response) => {
        }
        );
        setAmount(0);
      }
    const onAddFundsHandler = () => {
        setAddFunds(true);
    }
    const onCloseHandler = () => {
        setAddFunds(false);
    }
    return(
        <div className="wallet-container">
      <h1>{`${wallet && wallet.username}'s Wallet`}</h1>
      <div className="balance-container">
        <h2>Total Balance:</h2>
        <p className="balance">${wallet && wallet.totalBalance}</p>
      </div>
      <button className="add-funds-btn" onClick={onAddFundsHandler}>
        Add Funds
      </button>
      {addFunds && <AddFunds onClose={onCloseHandler} wallet = {wallet}/>}
    </div>

    );
}

export default Wallet;
