import React, { useState } from 'react';
import "./AddFunds.css"
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { SnackbarContent, SnackbarProvider, useSnackbar } from 'notistack';
const API_URL = "http://localhost:8080/wallets/";

function AddFunds(props) {
  const { enqueueSnackbar } = useSnackbar();
  const [amount, setAmount] = useState(0);
  const wallet = props.wallet;
  let navigate = useNavigate();

//   function handleSubmit(event) {
//     event.preventDefault();
//     console.log(amount);
//     console.log(API_URL + `${wallet.username}/balance`);
//     axios.post(API_URL + `${wallet.username}/balance`,{balance:amount}).then(
//         (response) => {enqueueSnackbar('Transfer completed successfully', { variant: 'success' });
//         navigate('/wallet')
//     }
//     );
//     setAmount(0);
//   }
  const submitHandler = ()=>{
    props.handleSubmit();
  }
  const onAmountChangeHandler = (event)=>{
    setAmount(event.target.value);
  }
  const handleBackClick = (event)=>{
    event.preventDefault();
    
    setAmount(0)
    props.onClose();
  }
  return (
    <div className="overlay">
      <div className="overlay-content">
        <h2>Add Funds</h2>
        <form className="add-funds-form" onSubmit={submitHandler}>
          <label htmlFor="amount">Amount:</label>
          <input type="number" id="amount" name="amount" onChange={onAmountChangeHandler}/>


          <button type="submit" className="add-funds-btn">
            Add Funds
          </button>
        </form>

        <button type="button" className="back-btn" onClick={handleBackClick}>
          Back
        </button>
        <SnackbarProvider/>
      </div>
    </div>
  );

}

export default AddFunds;
